window.onload=function(){for(var e=document.getElementById("carousel2"),t=e.querySelectorAll("li"),l=t.querySelector("img"),n=0;n<t.length;n++){t[n].style.position="relative",l[n].setAttribute("class","thumbnail zoom");var o=document.createElement("span");o.style.cssText="position:absolute;left:0;top:0",o.innerHTML=n+1,t[n].appendChild(o)}var r=100,c=1,i=document.getElementById("carousel2"),a=i.querySelector("ul"),u=i.querySelectorAll("li"),s=0;i.querySelector(".prev").onclick=function(){s=Math.min(s+r*c,0),a.style.marginLeft=s+"px"},i.querySelector(".next").onclick=function(){s=Math.max(s-r*c,-r*(u.length-c)),a.style.marginLeft=s+"px"}};